const o=x=>{if(!x||x.length<3)return 0;const y=[];x.forEach(t=>{y.push({x:t.x1,y:t.y1}),y.push({x:t.x2,y:t.y2})});const e=Array.from(new Map(y.map(t=>[`${t.x},${t.y}`,t])).values());if(e.length<3)return 0;const r=e.reduce((t,n)=>({x:t.x+n.x,y:t.y+n.y}),{x:0,y:0});r.x/=e.length,r.y/=e.length,e.sort((t,n)=>Math.atan2(t.y-r.y,t.x-r.x)-Math.atan2(n.y-r.y,n.x-r.x));let a=0;for(let t=0;t<e.length;t++){const n=(t+1)%e.length;a+=e[t].x*e[n].y,a-=e[n].x*e[t].y}return Math.abs(a/2)};export{o as c};
